const Config = {
  PROTOCOL: "ws:",
  HOST: "//localhost",
  PORT: ":7888"
}

const Socket = (function() {

  function createInstance() {
    const address = Config.PROTOCOL + Config.HOST + Config.PORT
    const socket = new WebSocket(address)
    console.log("Created a Socket instance at: " + address)
    return socket;
  }

  return {
    getNewInstance: function() {
      var instance = createInstance()
      return instance
    }
  }


})();

export function subscribeForPushNotifications(subscriptionSubject, callback) {

  const socket = Socket.getNewInstance()

  socket.onopen = () => {
    const msg = JSON.stringify({type:"SUBSCRIBE", subject:subscriptionSubject})
    socket.send(msg)
  }

  socket.onmessage = (msg) => {
    console.log("SOCKET: " + msg.data)
    callback()
  }
}

export default Socket;
