const Config = {
  PROTOCOL: "wss:",
  HOST: "//localhost",
  PORT: ":7888"
}

const Socket = (function() {

  let instance;

  function createInstance() {
    const socket = new WebSocket(Config.PROTOCOL + Config.HOST + Config.PORT)
    return socket;
  }

  return {
    getInstance: function() {
      if(!instance) {
        createInstance()
      }
      return instance
    }
  }


})();

export default Socket;
